function Html() {
  const [text, setText] = useState('HTML, which stands for HyperText Markup Language, is a markup language used to create and structure content on the web...');
  const [translatedText, setTranslatedText] = useState('');
  const [targetLanguage, setTargetLanguage] = useState('ru');

  const translateText = () => {
    // здесь должен быть код для отправки запроса к API Google Translate
    // и обновления состояния translatedText новым переведенным текстом
  }

  const handleLanguageChange = (event) => {
    setTargetLanguage(event.target.value);
  }

  return (
    <div className='java-script'>
      <h1>HTML</h1> 
      <p>{translatedText || text}</p>
      <div>
        <button onClick={translateText}>Перевести</button>
        <select onChange={handleLanguageChange} value={targetLanguage}>
          <option value="ru">Русский</option>
          <option value="hy">Հայերեն</option>
        </select>
      </div>
    </div>
  );
}

export default Html;
