import React from 'react';
import { NavLink } from 'react-router-dom';
import './styles.css'; // импортируем CSS-файл

function Courses() {
  return (
    <div className="card"> {/* обернули всё в карточку */}
      <div className="content">
        <NavLink to='/javascript' className="flex-item">
          <p>JavaScript ES5/ES6</p>
        </NavLink>
        <NavLink to='/html' className="flex-item">
          <p>HTML</p>
        </NavLink>
        <NavLink to='/reactjs' className="flex-item">
          <p>ReactJS</p>
        </NavLink>
      </div>
    </div>
  );
}

export default Courses;
