import React, { useState } from 'react';


function JsEs5_1() {
  const [language, setLanguage] = useState('en'); // язык по умолчанию - английский

  // функция, которая будет вызываться при нажатии на кнопку перевода
  function handleTranslate() {
    if (language === 'en') {
      setLanguage('ru'); // если язык английский, переводим на русский
    } else {
      setLanguage('en'); // если язык русский, переводим на английский
    }
  }

  return (
    <div className='java-script'>
      <h1>JavaScript</h1>
      <Button variant='contained' color='primary' onClick={handleTranslate}>
        {language === 'en' ? 'Перевести на русский' : 'Translate to English'}
      </Button>
      <p>
        {language === 'en'
          ? 'JavaScript was created in 1995 by Brendan Eich while he was working at Netscape Communications Corporation. The language was originally called Mocha, but was later renamed to LiveScript before finally being named JavaScript. The name change was mainly due to marketing reasons and to capitalize on the popularity of Java at the time. Brendan Eich is an American technologist and programmer who has worked for companies like Mozilla, Netscape, and Brave Software. He is considered to be the creator of JavaScript and has made significant contributions to other programming languages as well.'
          : 'JavaScript был создан в 1995 году Бренданом Айком (Brendan Eich) во время работы в компании Netscape Communications Corporation. Изначально язык назывался Mocha, но позже был переименован в LiveScript, а затем в JavaScript. Изменение названия было связано в основном с маркетинговыми целями и капитализацией на популярности Java в то время. Брендан Айк - американский технолог и программист, работавший в компаниях Mozilla, Netscape и Brave Software. Он считается создателем JavaScript и внес значительный вклад в другие языки программирования.'}
      </p>
      <h3>{language === 'en' ? 'ECMAScript defines 7 data types:' : 'ECMAScript определяет 7 типов данных:'}</h3>
      {/* остальной код компонента */}
    </div>
  );
}

export default JsEs5_1;
