import React, { useState } from 'react';

function Html() {
  // состояние языка по умолчанию
  const [language, setLanguage] = useState('en');

  // объект с текстом на разных языках
  const texts = {
    en: {
      title: 'HTML',
      content: 'HTML, which stands for HyperText Markup Language, is a markup language used to create and structure' +
      'content on the web. It is the backbone of every website and serves as the building blocks of web pages.'+ 
      'HTML is a declarative language, meaning it is used to describe the content and structure of a web page,'+ 
      'but not necessarily its appearance.',

    },
    ru: {
      title: 'HTML',
      content: 'HTML, что означает язык гипертекстовой разметки, является языком разметки, используемым для создания и структурирования контента в Интернете ...',
    },
    hy: {
      title: 'HTML',
      content: 'HTML, որը նշանակում է HyperText Markup Language, նշանակվում է նաև նշանակում է կառուցելու և կառուցելու բոլոր բովանդակությունները վեբում ։...',
    }
  };

  // функция, которая изменяет состояние языка при нажатии на кнопку
  const handleLanguageChange = (lang) => {
    setLanguage(lang);
  }

  return (
    <div className='java-script'>
       <button onClick={() => handleLanguageChange('en')}>English</button>
      <button onClick={() => handleLanguageChange('ru')}>Русский</button>
      <button onClick={() => handleLanguageChange('hy')}>Հայերեն</button>
      <h1>{texts[language].title}</h1>
      <p>{texts[language].content}</p>

      {/* кнопки для изменения языка */}

    </div>
  );
}

export default Html;





































